import React, { useContext } from "react";
import "./NextEvent.css";
import {dateFormatDbToView} from '../../Utils/stringFunction'
import { Tooltip } from "react-tooltip";
import { Link } from "react-router-dom";
import { UserContext } from "../../Context/AuthContext";

const NextEvent = ({ title, description, eventDate, idEvent }) => {
    const {userData} = useContext(UserContext)


    async function conectar(idEvent) {
      try {     //ROTA DO PROPRIO SWAGGER POR CAUSA DO DUPLO ID REQUISITADO NA FUNCAO DA INTERFACE
        const promise = await api.get(`${commentaryResourceGetById}?idUsuario=${userData.userId}&idEvento=${idEventoConnect}`)
        

        } catch (error) {
          alert("erro na api listar minhas")}
    }
  return (

    <article className="event-card">
      <h2 className="event-card__title">{title}</h2>
      
      <p className="event-card__description"
       data-tooltip-id={idEvent}
       data-tooltip-content={description}
       data-tooltip-place="top">

        <Tooltip id={idEvent} className="tooltip"/>
        {description.substr(0, 15)}...

        </p>
      
      <p className="event-card__description">{ dateFormatDbToView(eventDate) }</p>

      <Link to={"/comentarios-eventos"} onClick={() => {userData.role === "comum" ? conectar(idEvent) : to="/comentarios-eventos"}}  className="event-card__connect-link">Conectar</Link>
    </article>

  );
};

export default NextEvent;
