import React, { useContext, useEffect, useState } from "react";
import Header from "../../components/Header/Header";
import MainContent from "../../components/MainContent/MainContent";
import Title from "../../components/Title/Title";
import Table from "./TableEvA/TableEvA";
import Container from "../../components/Container/Container";
import { SelectForm } from "../../components/FormComponents/FormComponents";
import Spinner from "../../components/Spinner/Spinner";
import Modal from "../../components/Modal/Modal";
import api, { presencesEventResource } from "../../Services/Service";
import setNotifyUser from "../../components/Notification/Notification"
import {
    eventsResource,
    typeEventResource,
    nextEventsResource,
    typeEventResourceDelete,
    instituicaoResource,
    myEventsResource
  } from "../../Services/Service";
import "./EventosAlunoPage.css";
import { UserContext } from "../../Context/AuthContext";
import TableEvA from "./TableEvA/TableEvA";



const EventosAlunoPage = () => {

   // recupera os dados globais do usuário
   const { userData, setUserData } = useContext(UserContext);

  // state do menu mobile
  const [exibeNavbar, setExibeNavbar] = useState(false);
  const [eventos, setEventos] = useState([]);
  // select mocado
  const [quaisEventos, setQuaisEventos] = useState([
    { value: "1", text: "Todos os eventos" },
    { value: "2", text: "Meus eventos" },
  ]);

  const [tipoEvento, setTipoEvento] = useState([]); 
  const [myEvents, setMyEvents] = useState([]); 
  const [op, setOp] = useState("1"); 
  const [showSpinner, setShowSpinner] = useState(false);
  const [showModal, setShowModal] = useState(false);

 

  const verificaPresenca = (arrAllEvents, eventsUser) => {

    for(let x = 0; x < arrAllEvents.length; x++){
for(let i = 0; i < eventsUser.length; i++) {
if (arrAllEvents[x].idEvento === eventsUser[i].evento.idEvento) {
    arrAllEvents[x].situacao = true;
    arrAllEvents[x].idPesencaEvento = eventsUser[i].idPesencaEvento
    break;
}

}

    }

return arrAllEvents;
}
 
  useEffect(() => { LoadEvent() }, [op, userData.userId]);
  
   
  



//   toggle meus eventos ou todos os eventos
  // function myEvents(tpEvent) {
  //   setOp(tpEvent);
  // }

  async function loadMyComentary(idComentary) {
    return "????";
  }

  const showHideModal = () => {
    setShowModal(showModal ? false : true);
  };

  const commentaryRemove = () => {
    alert("Remover o comentário");
  };

  async function handleConnect(eventId, whatfunction, presencaId = null) {

   
      if (whatfunction === "connect") {
        
        try {const promise = await api.post(presencesEventResource, {situacao: true,
          idUsuario: userData.userId,
          idEvento:  eventId})

          if (promise.status === 201) {
            alert("presenca confirmada")
          }

          
          
        } catch (error) {
          
        }
        return;
      }
     
  //unconnect
  try {
    const unconnect = await api.delete(`${presencesEventResource}${presencaId}`)
    if (unconnect.status === 201) {
      alert("presenca desconfirmada")
    }


  } catch (error) {
    alert("erro no unconnect")
    
  }
    
   
  }
  return (
    <>
  
      

      <MainContent>
        <Container>
          <Title titleText={"Eventos"} className="custom-title" />

          <SelectForm
            id="id-tipo-evento"
            name="tipo-evento"
            required={true}
            options={quaisEventos} // aqui o array dos tipos
            manipulationFunction={(e) => setOp(e.target.value)} // aqui só a variável state
            defaultValue={op}
            addClass="select-tp-evento"
          />

          <Table
           dados={eventos} 
           dados2={myEvents}
           opcaoAPI={op}
            fnConnect={handleConnect}
            fnShowModal={() => {
              showHideModal();
            }}
          />
          
        </Container>
      </MainContent>

      {/* SPINNER -Feito com position */}
      {showSpinner ? <Spinner /> : null}

      {showModal ? (
        <Modal
          userId={userData.userId}
          showHideModal={showHideModal}
          fnDelete={commentaryRemove}
        />
      ) : null}
    </>
  );
};

export default EventosAlunoPage;
