import React from "react";
import "./EventosPage.css";
import Title from "../../components/Title/Title";
import MainContent from "../../components/MainContent/MainContent";
import Container from "../../components/Container/Container";
import ImageIllustrator from "../../components/ImageIllustrator/ImageIllustrator";
import { useState } from "react";
import {
  eventsResource,
  typeEventResource,
  nextEventsResource,
  typeEventResourceDelete
} from "../../Services/Service";
import {
  ButtonForm,
  InputForm,
  LabelForm,
  SelectForm,
} from "../../components/FormComponents/FormComponents";
import api from "../../Services/Service";
import TableTp from "../../components/TableTp/TableTp";
import { useEffect } from "react";
import Notification from "../../components/Notification/Notification";
import Spinner from "../../components/Spinner/Spinner";


const EventosPage = () => {
//FUNCAO DE MUDAR DE TELA DE CADASTRO E EDIT
const [frmEdit, setFrmEdit] = useState(false); //Esta em modo de edicao ou nao

//VALOR DO INPUT TELA DE CADASTRO E EDIT
const [titulo, setTitulo] = useState("");

//Notification component state
const [notifyUser, setNotifyUser] = useState({});

//state para array de objetos de tipoEventos da API 
const [tipoEventos, setTipoEventos] = useState([]);

//Pegar o ID do evento para a tela de Edicao
const [idEvento, setIdEvento] = useState(null);



  return (
    <>
    {<Notification {...notifyUser} setNotifyUser={setNotifyUser} />}

      <MainContent>
        <section className="cadastro-evento-section">
          <Container>
            <div className="cadastro-evento__box">
              <Title titleText={"Cadastro Tipo de Eventos"} />

              <ImageIllustrator
                imageName={"tipo-evento"}
                alteText={"imagem de tipo evento"}
              />

              <form
                className="ftipo-evento"
                onSubmit={frmEdit ? handleUpdate : handleSubmit}
              >
                {!frmEdit ? (
                  //Cadastrar
                  <>
                    <InputForm
                      type={"text"}
                      id={"Titulo"}
                      required={"required"}
                      name={"Titulo"}
                      placeholder={"Digite aqui"}
                      onChange={(e) => {
                        setTitulo(e.target.value);
                      }}
                      value={titulo}
                    />

                    <ButtonForm
                      textButton={"Cadastrar"}
                      id={"cadastrar"}
                      name={"cadastrar"}
                      type={"submit"}
                    />
                    {/* <ButtonForm
                      textButton={"Magica"}
                      id={"cadastrar"}
                      name={"cadastrar"}
                      type={"submit"}
                      // manipulationFunction={magic}
                    /> */}

                  </>

                ) : (
                  //Editar
                  <>Tela de Edicao

                    <InputForm
                      type={"text"}
                      id={"Titulo"}
                      required={"required"}
                      name={"Titulo"}
                      placeholder={"Digite aqui"}
                      onChange={(e) => {
                        setTitulo(e.target.value);
                      }}
                      value={titulo}
                    />

                  <div className="buttons-editbox">

                   <ButtonForm
                      textButton={"Atualizar"}
                      id={"atualizar"}
                      name={"atualizar"}
                      type={"submit"}
                   addClass="button-component-middle"
                   manipulationFunction={handleUpdate}
                    />

                    <ButtonForm
                      textButton={"Cancelar"}
                      id={"cancelar"}
                      name={"cancelar"}
                      manipulationFunction={editActionAbort}
                      addClass="button-component-middle"
                    />

                  </div>
                  
                  
                  </>
                  
                )}
              </form>
            </div>
          </Container>
        </section>

        <section className="lista-eventos-section">
          <Container>

                  <Title
                  titleText={"Lista Tipo de Eventos"}
                  color="white"
                  />

                    <TableTp
                    dados={tipoEventos}
                    fnDelete={handleDelete}
                    fnUpdate={showEditForm}
                    />
          </Container>
        </section>
      </MainContent>
    </>
  );
};

export default EventosPage;
