import React, { useState } from "react";
import "./TipoEventosPage.css";
import Title from "../../components/Title/Title";
import MainContent from "../../components/MainContent/MainContent";
import Container from "../../components/Container/Container";
import ImageIllustrator from "../../components/ImageIllustrator/ImageIllustrator";
import {
  eventsResource,
  typeEventResource,
  nextEventsResource,
  typeEventResourceDelete
} from "../../Services/Service";
import {
  ButtonForm,
  InputForm,
  LabelForm,
  SelectForm,
} from "../../components/FormComponents/FormComponents";
import api from "../../Services/Service";
import TableTp from "../../components/TableTp/TableTp";
import { useEffect } from "react";

const TipoEventosPage = () => {
  //FUNCAO DE MUDAR DE TELA DE CADASTRO E EDIT
  const [frmEdit, setFrmEdit] = useState(false); //Esta em modo de edicao ou nao

  //VALOR DO INPUT TELA DE CADASTRO
  const [titulo, setTitulo] = useState("");



  const [tipoEventos, setTipoEventos] = useState([]);

useEffect(() => {
async function loadDataEvent() {

  try {
    const retorno = await api.get(typeEventResource)
    setTipoEventos(retorno.data)
   
  } catch (error) {
    alert("erro na api")
  }
  
}
loadDataEvent();}, [tipoEventos])



  async function handleSubmit(event) {
    event.preventDefault();

    if (titulo.trim().length < 3) {
      alert("O titulo deve ter ao menos 3 caracteres");
    }

    try {
      const retorno = await api.post(typeEventResource, { titulo: titulo });
      alert("Cadastrado");
      setTitulo("");
    } catch (error) {
      alert("Deu ruim no submit");
    }
  }

  function handleUpdate() {


    alert("Atualizado");
  }

function showUpdateForm() {
  
}

function editActionAbort() {
  
}

async function handleDelete(idEvento) {

  if (!document.confirm) {
    
  }

try {
  
const deletarData = await api.delete(`${typeEventResource}/${idEvento}`)

} catch (error) {
  alert("Deu ruim no Delete");

}

  
}

  return (
    <>
      <MainContent>
        <section className="cadastro-evento-section">
          <Container>
            <div className="cadastro-evento__box">
              <Title titleText={"Cadastro Tipo de Eventos"} />

              <ImageIllustrator
                imageName={"tipo-evento"}
                alteText={"imagem de tipo evento"}
              />

              <form
                className="ftipo-evento"
                onSubmit={frmEdit ? handleUpdate : handleSubmit}
              >
                {!frmEdit ? (
                  //Cadastrar
                  <>
                    <InputForm
                      type={"text"}
                      id={"Titulo"}
                      required={"required"}
                      name={"Titulo"}
                      placeholder={"Digite aqui"}
                      onChange={(e) => {
                        setTitulo(e.target.value);
                      }}
                      value={titulo}
                    />

                    <ButtonForm
                      textButton={"Cadastrar"}
                      id={"cadastrar"}
                      name={"cadastrar"}
                      type={"submit"}
                    />
                  </>
                ) : (
                  //Editar
                  <>Tela de Edicao</>
                )}
              </form>
            </div>
          </Container>
        </section>

        <section className="lista-eventos-section">
          <Container>

                  <Title
                  titleText={"Lista Tipo de Eventos"}
                  color="white"
                  />

                    <TableTp
                    dados={tipoEventos}
                    fnDelete={handleDelete}
                    fnUpdate={handleUpdate}
                    />
          </Container>
        </section>
      </MainContent>
    </>
  );
};

export default TipoEventosPage;
